INSERT INTO ListItem (ParentId, Name, [Description], Value,SortOrder, StatusId, LastUpdateTimestamp, LastUpdateAccountId) SELECT liat.ListItemId, 'Death', 'Death', '1000',1000, lisa.ListItemId, getdate(), (SELECT TOP 1 AccountId FROM Account ORDER BY DateCreatedTimestamp) FROM ListItem lis INNER JOIN ListItem lisa ON (lis.ListItemId = lisa.ParentId AND lis.Name = 'Status' AND lisa.Name = 'Active') INNER JOIN ListItem liat ON (liat.ParentId IS NULL AND liat.Name = 'ImpairmentClass');
INSERT INTO ListItem (ParentId, Name, [Description], Value,SortOrder, StatusId, LastUpdateTimestamp, LastUpdateAccountId)SELECT liat.ListItemId, 'Death', '', '1',1000, lisa.ListItemId, getdate(), (SELECT TOP 1 AccountId FROM Account ORDER BY DateCreatedTimestamp) FROM ListItem lis INNER JOIN ListItem lisa ON (lis.ListItemId = lisa.ParentId AND lis.Name = 'Status' AND lisa.Name = 'Active') INNER JOIN ListItem liat ON (liat.ParentId IS NOT NULL AND liat.Name = 'Death');